import { createStore } from "redux";
import { DECREMENT, INCREMENT } from "../../action/action";

const initialState = { count: 0};

 export function countData(state = initialState, action) {
  switch (action.type) {
    case INCREMENT:
      return { ...state, count: state.count + action.incrementBy };
    case DECREMENT:
      return { ...state, count: state.count - action.subtractBy };
    default:
      return state;
  }
}
