import React from "react";
import { useSelector, useDispatch } from "react-redux";

function Home() {
  const count = useSelector((state) => state.count);
  const dispatch = useDispatch();
  return (
    <div>
      <div>
      <h1 style={{color:"green"}}>Count: {count}</h1>
      <button onClick={() => dispatch({ type: "INCREMENT" ,incrementBy:5})}>Add 5</button>
      <button onClick={() => dispatch({ type: "DECREMENT",subtractBy:5 })}>Remove 5</button>
    </div>
    </div>
  );
}

export default Home;
